
public class ejercicio2u4 {

	private static String valora;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int N = 5;
	        double A = 4.56;
	        char C = 'a';

	        System.out.println("Variable N = " + N);
	        System.out.println("Variable A = " + A);
	        System.out.println("Variable C = " + C);

	        double suma = N + A;
	        double diferencia = A - N;
	        int valorNumerico = (int) C;

	        System.out.println(N + " + " + A + " = " + suma);
	        System.out.println(A + " - " + N + " = " + diferencia);
	        System.out.println("Valor numérico del carácter " + C + " = " + valorNumerico);
	        

	}

}
